package com.hualu.wifistart.smbsrc;import com.hualu.wifistart.R;import android.app.Activity;import android.net.Uri;import android.os.Bundle;import android.widget.MediaController;import android.widget.VideoView;public class VideoPlayActivity extends Activity {	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		this.setContentView(R.layout.media_player);		String httpString = (String)getIntent().getExtras().get("smbpath");		httpString = httpString.replaceFirst("smb", "http");		httpString = httpString.replaceFirst("mnt/", "");		Uri uri = Uri.parse(httpString);		VideoView videoView = (VideoView) this				.findViewById(R.id.videoView1);		videoView.setMediaController(new MediaController(this));		videoView.setVideoURI(uri);		videoView.start();		videoView.requestFocus();	}}